/**
 * type defined for image data with 7 variables
 * @name imageData
 * @typedef { sx: number; sy: number; sw: number; sh: number; dx: number; dy: number; scale: number }
 */
type imageData = {
    sx: number | undefined,
    sy: number | undefined,
    sw: number | undefined,
    sh: number | undefined,
    dx: number | undefined,
    dy: number | undefined,
    scale: number
}